palette_procs: ; openFile, readsteve_header, readPalette, copyPalette, addToPalette, setPalette
	; Opens a file.
	; INPUT: [bp+6] steve_filehandle offset, [bp+4] steve_filename offset
	; OUTPUT: None.
	openFile proc
		push bp
		mov bp, sp
		push ax dx di
		;;;;;;;;;;
			mov ax, 3D00h
			mov dx, offset steve_filename
			int 21h
			mov di, offset steve_filehandle
			mov [di], ax
		;;;;;;;;;;
		pop di dx ax bp
		ret
	openFile endp

	; Reads the steve_header of the file.
	; INPUT: [bp+6] steve_filehandle offset, [bp+4] steve_header offset
	; OUTPUT: None.
	readsteve_header proc
		push bp
		mov bp, sp
		push ax bx cx dx di
		;;;;;;;;;;
			mov ah,3fh
			mov di, offset steve_filehandle ; steve_filehandle offset
			mov bx, [di] ; steve_filehandle
			mov cx,54 ; number of bytes to read
			mov dx, offset steve_header
			int 21h
		;;;;;;;;;;
		pop di dx cx bx ax bp
		ret
	readsteve_header endp

	; Reads the palette of the file.
	; INPUT: [bp+6] steve_filehandle offset, [bp+4] palette offset.
	; OUTPUT: None.
	readPalette proc
		push bp
		mov bp, sp
		push ax bx cx dx di
		;;;;;;;;;;
			mov ax,3f00h
			mov di, offset steve_filehandle ; steve_filehandle offset
			mov bx, [di] ; steve_filehandle
			mov cx, 256*4 ; colors*bytes
			mov dx, offset palette ; palette offset
			int 21h
		;;;;;;;;;;
		pop di dx cx bx ax bp
		ret
	readPalette endp

	; Copies the file's palette to the video memory.
	; INPUT: [bp+6] steve_filehandle offset, [bp+4] palette offset.
	; OUTPUT: None.
	copyPalette proc
		push bp
		mov bp, sp
		pusha
		;;;;;;;;;;
			mov si, offset steve_filehandle ; steve_filehandle offset
			mov bx, [si] ; steve_filehandle
			mov si, offset palette ; palette offset
			mov cx, 256 ; colors
			mov dx,3C8h ; port
			xor al, al ; 0
			out dx,al
			; Copy starting color to port 3C8h

			; Copy palette itself to port 3C9h
			inc dx
			copyPaletteLoop:
				; Note: Colors in a BMP file are saved as BGR values rather than RGB .
				mov al,[si+2] ; Get red value .
				shr al,2 ; Max. is 255, but video palette maximal
				; value is 63. Therefore dividing by 4.
				out dx,al ; Send it .
				mov al,[si+1] ; Get green value .
				shr al,2
				out dx,al ; Send it .
				mov al,[si] ; Get blue value .
				shr al,2
				out dx,al ; Send it .
				add si,4 ; Point to next color .
				; (There is a null chr. after every color.)
			loop copyPaletteLoop
		;;;;;;;;;;
		popa
		pop bp
		ret
	copyPalette endp

	addToPalette proc
		pusha

		mov dx, 03c8h
		mov al, 0ffh ; color number
		out dx, al

		mov dx, 03c9h
		mov al, 50 ; red
		out dx, al
		mov al, 50 ; green
		out dx, al
		mov al, 50 ; blue
		out dx, al

		popa
	addToPalette endp ;54 colors

	; Set current palette to Steve's palette.
	; INPUT:
	; OUTPUT: None.
	setPalette proc
		push bp
		mov bp, sp
		pusha

		mov dx, offset steve
		add dx, 100*200-100

		set_loop:
			mov ah, 3fh
			mov si, offset steve_filehandle ; steve_filehandle offset
			mov bx, [si]
			mov cx,100
			int 21h
			sub dx, 100
			cmp dx, offset steve
			jae set_loop

		popa
		pop bp
		ret
	setPalette endp

other_procs: ; clear, delay, display_config
	; Clears screen.
	; INPUT: None.
	; OUTPUT: None.
	clear proc
		pusha

		xor cx, cx
		xor dx, dx
		mov al, 6

		sky_loop:
			mov ah, 0ch
			int 10h
			inc cx
			cmp cx, 1280
			jl sky_loop
			sub cx, 1280
			inc dx
			cmp dx, 1000
			jl sky_loop
			mov al, 5
			cmp dx, 1024
			jl sky_loop

		popa
		ret
	clear endp

	; Creates delay.
	; INPUT: None.
	; OUTPUT: None.
	delay proc
		push cx

		mov cx, 10
		d1:
		push cx
		mov cx, 0FFFFh
		d2:
		loop d2
		pop cx
		loop d1

		pop cx
		ret
	delay endp

	; Configures display.
	; INPUT: None.
	; OUTPUT: None.
	display_config proc
		pusha

		;INITIALIZE
		mov ax, @data
		mov ds, ax
		mov ax, 0A000h
		mov es, ax

		;GRAPHIC MODE (1280x1024x256)
		mov ax, 4F02h
		mov bx, 107h
		int 10h

		popa
		ret
	display_config endp

	; Checks if 'a' or 'd' keys have been pressed.
	; INPUT:
	; OUTPUT: None.
	move_test proc
		pusha

	  mov ah, 1
	  int 16h
	  cmp al, 0
	  je move_end

	  mov ah, 0
	  int 16h

		a:
		  cmp al, 'a'
		  jne d
		  sub [x], 50

		d:
		  cmp al, 'd'
		  jne q
		  add [x], 50

		q:
		  cmp al, 'q'
		  jne move_end
			mov ah, 4ch
			int 21h

		move_end:
			popa
			ret
	move_test endp

sprite_procs: ; show_sprite, hide_sprite
	; Prints sprite.
	; INPUT:
	; OUTPUT: None.
	show_sprite proc
		pusha

		mov ah, 0ch
		mov bx, offset steve
		mov si, [y]
		add si, 199
		mov di, [x]
		add di, 99

		sprite_loop:
			cmp [y], si
			je end_sprite_loop ; check if finished last line

			mov ch, [bx]
			cmp ch, 0ffh
			je back_pixel ; check if pixel is background pixel

			print_pixel:
				mov al, [bx]
				push bx
				mov cx, [x]
				mov dx, [y]
				int 10h
				pop bx

			back_pixel:
				inc bx
				cmp [x], di ; check if reached end of row
				je new_pixel_line

				inc [x]
				jmp sprite_loop

			new_pixel_line:
				inc [y] ; new line
				sub [x], 99 ; reset column
				jmp sprite_loop

		end_sprite_loop:
			sub [y], 199
			popa
			ret
	show_sprite endp

	; Erases sprite.
	; INPUT:
	; OUTPUT: None.
	hide_sprite proc
		pusha

		mov si, [y]
		add si, 199
		mov di, [x]
		add di, 99
		mov ah, 0ch


		hide_loop:
			cmp [y], si
			jge end_hide_loop ; check if finished last line

			hide_pixel:
				mov al, 6
				mov cx, [x]
				mov dx, [y]
				int 10h

			check:
				cmp [x], di ; check if reached end of row
				je new_pixel_line2

				inc [x]
				jmp hide_loop

			new_pixel_line2:
				inc [y] ; new line
				sub [x], 99 ; reset column
				jmp hide_loop

		end_hide_loop:
			sub [y], 199 ;41
			popa

		ret
	hide_sprite endp

main:
	call display_config
	call clear

	; open steve.bmp and set it's color palette
	call openFile
	call readsteve_header
	call readPalette
	call copyPalette
	call setPalette

	game:
		mov bx, 4

		fall:
			call show_sprite
			call hide_sprite
			call move_test
			add [y], bx
			cmp [y], 800
			jle fall
			mov [y], 800

		jump:
			call show_sprite
			call hide_sprite
			call move_test
			sub [y], bx
			cmp [y], 400
			jle game
			jmp jump

		mov ah, 4ch
		int 21h
end main
