show_sprite proc
	pusha
	mov bx, offset player
	mov ax, 0

	mov [y], 1
	mov [x], 1

	sprite_loop:
		cmp [y], 83 ; vertical
		jg end_sprite_loop

		push bx
		cmp [bx], 227
		je black_pixel

		print_pixel:
			mov al, [bx]
			mov bh, 0
			mov cx, [x]
			mov dx, [y]
			mov ah, 0ch
			int 10h

		black_pixel:
		pop bx
		cmp [x], 100 ; horizontal
		je new_pixel_line

		inc [x]
		inc bx
		jmp sprite_loop

		new_pixel_line:
		inc [y]
		sub [x], 99 ; horizontal-1
		inc bx
		jmp sprite_loop

	end_sprite_loop:
popa
ret
show_sprite endp

clear proc
	pusha

	mov bh, 0h
	mov cx, 0
	mov dx, 0
	mov al, 0bh

	loop1:
	mov ah, 0ch
	int 10h
	inc cx
	cmp cx, 1280
	jl loop1
	sub cx, 1280
	inc dx
	cmp dx, 1024
	jl loop1

	popa
	ret
clear endp

