display_config proc
	push ax
	push bx

	;GRAPHIC MODE (1280x1024x256)
	mov ax, 4F02h
	mov bx, 107h
	int 10h

	;ADVANCED DISPLAY
	mov ah, 0Bh         ; set the cool palette
    mov bx, 0101h       ; (cyan, magenta, white)
    int 10h

	pop bx
	pop ax
	ret
display_config endp


main:
    mov ax, 4           ; set CGA 320x200
    int 10h

    mov ah, 0Bh         ; set the cool palette
    mov bx, 0101h       ; (cyan, magenta, white)
    int 10h

    xor bx, bx          ; low intensity, black background
    int 10h

    mov ax, 0B800h      ; draw some pixels in each colour
    mov es, ax
    mov di, 0
    mov bx, 320
    xor ax, ax
    mov cx, bx
    rep stosw
    inc ax
    mov cx, bx
    rep stosw
    inc ax
    mov cx, bx
    rep stosw
    inc ax
    mov cx, bx
    rep stosw

    mov ah, 1           ; wait for a character
    int 21h

    mov ah, 0Bh
    mov bx, 0010h       ; high intensity
    int 10h

    mov ah, 1           ; wait for a character
    int 21h

    mov ah, 0Bh         ; set the warm palette
    mov bx, 0100h       ; (red, green, brown)
    int 10h

    mov ah, 1           ; wait for a character
    int 21h

    mov ah, 0Bh
    xor bx, bx          ; low intensity
    int 10h

    mov ah, 1           ; wait for a character
    int 21h

    mov ax, 4C00h       ; exit
    int 21h
.exit
end main
